 * repository methods below.
 */
class ObjectRepository extends EntityRepository
{
    public function search($params=array())
    {
        $searchTerm = $params['term'];
        $categoryIds = $params['category'];
        $criteria = array(
            'title' => ''
        );
        return $this->findBy($criteria);
    }

}